import React from 'react';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      img: false
    }
  }
  componentDidMount() {
    // fetch('api.openweathermap.org/data/2.5/weather?q=Riverton&appid=f6c3ead84e4c7b12a7cbe6d6dd18c102')
    fetch("https://movie-database-imdb-alternative.p.rapidapi.com/?page=1&r=json&s=Hot-summer-nights", {
	    "method": "GET",
	    "headers": {
		    "x-rapidapi-host": "movie-database-imdb-alternative.p.rapidapi.com",
	    	"x-rapidapi-key": "672e71f89emsh8fc41052f8d3281p177d4cjsn86ebdbc19af9"
	    }
    })
    .then(res => res.json())
    .then(json => {
      console.log(json)
      this.setState({
        img: json.Search[0].Poster
      })
    })
  }
  render() {
    if(!this.state.img){
      return (
        <img src={this.state.img} />
      )
    } else {
      return <div>Loading...</div>
    }
  }
}

export default App;
