import React from 'react'
import store from '../store';

export default class SearchPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            inputVal: ''
        }
    }
    componentDidMount() {
        this.unsubscribe = store.subscribe(() => this.forceUpdate());
    }
    updateVal = (e) => {
        this.setState({
            inputVal: e.target.value
        })
    }
    searchMovie = () => {
        store.dispatch({
            type: "SEARCH_MOVIE",
            movie: this.state.inputVal
        })
        this.setState({
            inputVal: ''
        })
    }
    render() {
        return (
            <div>
                <input onChange={this.updateVal} value={this.state.inputVal} />
                <button onClick={this.searchMovie}>Search</button>
            </div>
        )
    }
}
