import React from 'react';
import $ from 'jquery';
import store from '../store/index';

export default class Modal extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            inputVal: ''
        }
    }
    inputChange = (e) => {
        this.setState({
            inputVal: e.target.value
        })
    }
    closeModal() {
        $(".movies-list-page").removeClass('modal-open');
        $('.modal-wrapper').fadeOut(400);
    }  
    newList() {
        store.dispatch({
            type: "ADD_LIST",
            listName: this.state.inputVal
        })
        this.setState({
            inputVal: ''
        })
    }
    render() {
        return (
            <div className="modal-wrapper">
                <div className="modal-content-wrapper">
                    <div className="close" onClick={this.closeModal}>
                        X
                    </div>
                    <div className="new-list-wrapper">
                        <button onClick={this.newList}>Create New List</button>
                        <input onChange={this.inputChange} value={this.state.inputVal} type="text" className="new-list-input" />
                    </div>
                </div>
            </div>
        )
    }
}
