import { initState } from '../index';

const reducer = (state = initState, action) => {
    if(action.type === "SEARCH_MOVIE") {
        return {
            ...state,
            loaded: true,
            results: action.results
        }
    } 
    else if(action.type === "ADD_LIST") {
        return {
            ...state, 
            list: state.list.concat(action.item)
        }
    }
    else {
        return state;
    }
}

export default reducer;
