import React from 'react'
import store from '../store';

export default class SearchPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            inputVal: ''
        }
    }
    componentDidMount() {
        this.unsubscribe = store.subscribe(() => this.forceUpdate());
    }
    updateVal = (e) => {
        this.setState({
            inputVal: e.target.value
        })
    }
    searchMovie = () => {
        fetch(`https://movie-database-imdb-alternative.p.rapidapi.com/?page=1&r=json&s=${this.state.inputVal}`, {
            "method": "GET",
            "headers": {
                "x-rapidapi-host": "movie-database-imdb-alternative.p.rapidapi.com",
                "x-rapidapi-key": "672e71f89emsh8fc41052f8d3281p177d4cjsn86ebdbc19af9"
            }
        })
        .then(res => res.json())
        .then(json => {
            store.dispatch({
                type: "SEARCH_MOVIE",
                results: json
            })
        })
        this.setState({
            inputVal: ''
        })
    }
    render() {
        return (
            <div className="search-page-wrapper">
                <div className="search-bar-wrapper">
                    <input onChange={this.updateVal} value={this.state.inputVal} />
                    <button onClick={this.searchMovie}>Search</button>
                </div>
            </div>
        )
    }
}
