import React from 'react';
import { Link } from 'react-router-dom';
import store from '../store/index';
import MovieItem from './MovieItem';
import axios from 'axios';

export default class ViewPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            inputval: ''
        }
    }
    componentDidMount() {
        this.unsubscribe = store.subscribe(() => this.forceUpdate());
    }
    submit = () => {
        axios.post('http://localhost:3000/view-listings', this.state.inputVal).then(res => {
            console.log(res);
        })
    }
    onChange = (e) => {
        this.setState({
            inputval: e.target.value
        })
    }
    componentWillUnmount() {
        this.unsubscribe();
    }
    render() {
        if(!store.getState().loaded) {
            return (
                <div className="loading-wrapper">
                    <div className="loading"></div>
                </div>
            )
        } else {
            return (
                <div className="movies-list-page">
                    <form action="/view-listings" onSubmit={this.submit}>
                        <input name="name" value={this.state.inputval} onChange={this.onChange}/>
                        <button type="submit">Submit</button>
                    </form>
                    {store.getState().results.Search.map((itm, idx) => (
                        <MovieItem key={idx} movieInfo={itm}/>
                    ))}
                </div>
            )
        }
    }
}
